mapper.select:
SELECT * FROM
    (
        SELECT ROWNUM RNUM, B.*, BI.STORED_FILE_NAME IMAGE_NAME
        FROM
            (
            SELECT *
            FROM BOOK
            
	            DEL_FLAG = 'N'
	            AND NAME LIKE '%' || #{name} || '%'
	            AND WRITER LIKE '%' || #{writer} || '%'
	            AND PUBLISH LIKE '%' || #{publish} || '%'
	            
	            	
	            		
	            			
	            				AND PUBLISH_DATE BETWEEN #{publishDateStart} AND #{publishDateEnd}
	            			
	            			
	            				AND PUBLISH_DATE >= #{publishDateStart}
	            			
	            		
					
					
						
							AND PUBLISH_DATE <= #{publishDateEnd}
						
					
	            
	            
	            	AND CATEGORY_CODE BETWEEN #{categoryStart} AND #{categoryEnd}
	            
            
            ) B
        JOIN BOOK_IMAGE BI
        ON B.IMAGE_NO = BI.NO
        ORDER BY B.NO
    )
WHERE RNUM BETWEEN #{startPage} AND #{endPage}

        SELECT COUNT(*)
        FROM
            (
            SELECT *
            FROM BOOK
            
	            DEL_FLAG = 'N'
	            AND NAME LIKE '%' || #{name} || '%'
	            AND WRITER LIKE '%' || #{writer} || '%'
	            AND PUBLISH LIKE '%' || #{publish} || '%'
	            
	            	
	            		
	            			
	            				AND PUBLISH_DATE BETWEEN #{publishDateStart} AND #{publishDateEnd}
	            			
	            			
	            				AND PUBLISH_DATE >= #{publishDateStart}
	            			
	            		
					
					
						
							AND PUBLISH_DATE <= #{publishDateEnd}
						
					
	            
	            
	            	AND CATEGORY_CODE BETWEEN #{categoryStart} AND #{categoryEnd}
	            
            
            ) B
        JOIN BOOK_IMAGE BI
        ON B.IMAGE_NO = BI.NO
