mapper.select:
SELECT * FROM
    (
        SELECT ROWNUM RNUM, M.*
        FROM
            (
            SELECT *
            FROM MEMBER
            
	            DEL_FLAG = 'N'
	            AND NAME LIKE '%' || #{name} || '%'
	            AND EMAIL LIKE '%' || #{email} || '%'
	            
	            	
	            		
	            			
	            				AND CREATED_DATE BETWEEN #{joinDateStart} AND #{joinDateEnd}
	            			
	            			
	            				AND CREATED_DATE >= #{joinDateStart}
	            			
	            		
					
					
						
							AND CREATED_DATE <= #{joinDateEnd}
						
					
	            
            
            ORDER BY NO DESC
           ) M
    )
WHERE RNUM BETWEEN #{startPage} AND #{endPage}

		SELECT COUNT(*)
		FROM MEMBER
	   
           DEL_FLAG = 'N'
           AND NAME LIKE '%' || #{name} || '%'
           AND EMAIL LIKE '%' || #{email} || '%'
           
           	
           		
           			
           				AND CREATED_DATE BETWEEN #{joinDateStart} AND #{joinDateEnd}
           			
           			
           				AND CREATED_DATE >= #{joinDateStart}
           			
           		
			
			
				
					AND CREATED_DATE <= #{joinDateEnd}

SELECT COUNT(*)
FROM MEMBER
WHERE EMAIL = #{email}

SELECT COUNT(*)
FROM MEMBER
WHERE PHONE = #{phone}

SELECT NO, GRADE_CODE, NAME, EMAIL, PHONE, ADDRESS, CREATED_DATE, PASSWORD
FROM MEMBER
WHERE EMAIL = #{email}
AND PASSWORD = #{password}

SELECT NO, EMAIL
FROM MEMBER
WHERE NAME = #{userName}
AND PHONE = #{phone}

mapper.update:
UPDATE MEMBER

	 EMAIL=#{no}, PASSWORD=#{no}, NAME=#{no}, PHONE=#{no}, ADDRESS=#{no}, DEL_FLAG='Y'

WHERE NO = #{no}

UPDATE MEMBER
PASSWORD = #{password}, NAME = #{name}, PHONE = #{phone}, ADDRESS = #{address}, MODIFIED_DATE = SYSDATE
WHERE NO = #{no}

