mapper.insert:
SELECT NOTICE_NO_SEQ.NEXTVAL FROM DUAL
INSERT INTO NOTICE
VALUE(NO, MEMBER_NO, CATEGORY_CODE, TITLE, CONTENT, START_DATE, END_DATE, CREATED_DATE, MODIFIED_DATE) VALUES(#{no}, #{memberNo}, #{categoryCode}, #{title}, #{content}
, null, null
, #{startDate}, #{endDate}
, SYSDATE, SYSDATE)

mapper.update:
UPDATE NOTICE SET CATEGORY_CODE = #{categoryCode}, TITLE = #{title}, CONTENT = #{content},
START_DATE = #{startDate}, END_DATE = #{endDate}
START_DATE = null, END_DATE = null
WHERE NO = #{no}
